package chapter01;

public class FormatSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		double b =3.24;
		System.out.printf("%10d\n", a);
		System.out.printf("x %8.5f\n", b);
		System.out.printf("-----------\n");
		System.out.printf("%10.6f\n", a*b);

	}

}
